using ll = long long;
class Solution {
public:
    ll mod = 1e9 + 7;
    ll dp[2001][51];
    ll f(int i, int pre1, vector<int> &nums)
    {
        // cout << i << " " << pre1 << "\n";
        if(i >= nums.size()) return 1;
        if(dp[i][pre1] != -1) return dp[i][pre1];
        ll re = 0;
        ll pre2 = INT64_MAX;
        if(i) pre2 = nums[i - 1] - pre1;
        // cout << pre2 << "\n";
        for(int j = pre1; j <= nums[i]; j++){
            if(nums[i] - j <= pre2){
                re += f(i + 1, j, nums);
                re %= mod;
            }
            // else break;
        }
        return dp[i][pre1] = re;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return f(0, 0, nums);
    }
};
