class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @cache
        def helper(ind, last1):
            if ind == n:
                return 1

            last2 = nums[ind - 1] - last1
            res = 0
            for curr1 in range(last1, nums[ind] + 1):
                curr2 = nums[ind] - curr1
                if curr1 >= last1 and curr2 <= last2:
                    res += helper(ind + 1, curr1)
            return res % MOD

        return sum(helper(1, l) for l in range(nums[0] + 1)) % MOD
