class Solution {
    int mod = 1e9+7;
    vector<vector<int>> dp;
    int solve(vector<int>& nums, int i, int prev){
        if(i==nums.size()){return 1;}
        long long ans = 0;

        if(prev==-1){
            for(int cur = 0;cur<=nums[i];cur++){
                ans = (ans + solve(nums,i+1,cur))%mod;
            }
        }else{
            if(dp[i][prev]!=-1)return dp[i][prev];
            for(int cur=prev;cur<=nums[i];cur++){
                if(nums[i]-cur<=nums[i-1]-prev){
                    ans = (ans + solve(nums,i+1,cur))%mod;
                }
            }
        }
        if(prev!=-1)dp[i][prev]=ans;
        return ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(),vector<int>(51,-1));
        return solve(nums,0,-1);

    }
};
