MOD = 10**9 + 7

class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        n = len(nums)
        dp = [[0] * 51 for _ in range(51)]

        for x in range(nums[0] + 1):
            y = nums[0] - x
            dp[x][y] = 1

        for i in range(1, n):
            new_dp = [[0] * 51 for _ in range(51)]
            for x in range(51):
                for y in range(51):
                    if dp[x][y] > 0:
                        for nx in range(x, nums[i] + 1):
                            ny = nums[i] - nx
                            if ny <= y:
                                new_dp[nx][ny] = (new_dp[nx][ny] + dp[x][y]) % MOD
            dp = new_dp

        result = 0
        for x in range(51):
            for y in range(51):
                result = (result + dp[x][y]) % MOD

        return result
