ll dp[51][2002];

ll addmod(ll a, ll b) {
	a %= mod;
	b %= mod;

	return (a + b) % mod;
}

int n;

class Solution {
public:

	ll countWays(int lastVal, int pos, vii &nums) {
		if (pos == n) return 1;
		ll &ans = dp[lastVal][pos];
		if (ans != -1) return ans;
		ans = 0;

		int a = lastVal;
		int b = nums[pos - 1] - a;

		int x1 = a, y1 = nums[pos];
		int x2 = 0, y2 = b;

		fo(i, x1, y1) {
			int j = nums[pos] - i;
			if (j >= x2 and j <= y2) {
				ans = addmod(ans, countWays(i, pos + 1, nums));
			}
		}

		return ans;

	}

	int countOfPairs(vector<int>& nums) {
		n = sz(nums);

		int mx = 0;
		for (auto &i : nums) mx = max(mx, i);

		fo(i, 0, n - 1) {
			fo(j, 0, mx) dp[j][i] = -1;
		}

		ll ans = 0;
		fo(i, 0, nums[0]) {
			ans = addmod(ans, countWays(i, 1, nums));
		}

		return ans;
	}
};
