class Solution {
public:
    const int M = 1e9 + 7;
    int dp[53][52][2005], ans;

    int countPairs(int i, int x, int y, vector<int>& nums) 
    {
        if (i == (int)nums.size()) 
            return 1;

        if (dp[x][y][i] != -1) 
            return dp[x][y][i];

        ans = 0;

        for(int j = x; j <= nums[i]; j++) 
        {
            if (y + j >= nums[i]) 
                ans = (ans + countPairs(i + 1, j, nums[i] - j, nums)) % M;
        }

        return dp[x][y][i] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp)); 
        return countPairs(0, 0, nums[0], nums);
    }
};
