class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        mod=10**9+7

        @lru_cache(None)
        def fun(i,past_i,past_j):
            if i>=n:
                return 1
            ans=0
            for val in range(0,nums[i]+1):
                if val>=past_i and nums[i]-val<=past_j:
                    ans+=(fun(i+1,val,nums[i]-val))
            return ans%mod

        return fun(0,0,math.inf)
