class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n = len(nums)

        @cache
        def dfs(i: int, x: int, y: int) -> int:
            if i == n:
                return 1
            ans = 0
            for v1 in range(x, nums[i]+1):
                v2 = nums[i] - v1
                if v2 <= y:
                    ans += dfs(i+1, v1, v2)
            return ans % MOD

        return dfs(0, 0, 50)
