#define ll long long
ll mod = 1e9+7;

class Solution {
public:
    int f(int i,int prev,int prev1,vector<int> &a,vector<vector<vector<int>>> &dp){
        if(i==a.size()){
            return 1;
        }
        if(dp[i][prev][prev1]!=-1){
            return dp[i][prev][prev1];
        }

        int ans=0;
        for(int k=prev;k<=50;k++){
            int l = a[i] - k;
            if(l<=50 && l>=0 && l<=prev1){
                ans = (ans + f(i+1,k,l,a,dp))%mod;
            }
        }
        return dp[i][prev][prev1]=ans%mod;
    }

    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        int ans = f(0,0,50,a,dp);
        return ans;
    }
};
