class Solution {
public:
    const int mod = 1e9 + 7;
    vector<vector<int>> dp;
    int fun(int i,int pv1,vector<int> &nums){
        if(i == nums.size()) return 1;
        int ans = 0;
        if(dp[i][pv1 + 1] != -1) return dp[i][pv1 + 1];
        if(pv1 == -1){
            for(int k = 0; k <= min(50,nums[i]);k++){
                ans = (ans + fun(i + 1,k,nums)) % mod;
            }
        }
        else{
            for(int k = 0; k <=  min(50,nums[i]);k++){
                int pv2 = nums[i - 1] - pv1;
                if(pv1 <= k && pv2 >= (nums[i] - k) ){
                    ans = (ans + fun(i + 1,k,nums)) % mod;
                }
            }
        }
        return dp[i][pv1 + 1] = ans;

    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size() + 1,vector<int> (55 , -1));
        int ans = fun(0,-1,nums);
        return ans;
    }
};
