class Solution {
public:
    int mod = 1e9 + 7;
    long long dp[2001][53][53];
    int solve(vector<int> &nums, int idx, int prev1, int prev2){
        if(idx == nums.size()) return 1;
        if(dp[idx][prev1][prev2] != -1) return dp[idx][prev1][prev2];
        int ans = 0;
        for(int k = prev1; k <= nums[idx]; k++){
            if(nums[idx] - k <= prev2) ans = (ans % mod + solve(nums, idx + 1, k, nums[idx] - k) % mod) % mod;
        }
        return dp[idx][prev1][prev2] = ans % mod;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, 0, 52) % mod;
    }
};
