class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD=1_000_000_007 
        @cache
        def dp(i,j,itr):
            if itr==len(nums):return 1
            cnts=0
            for k in range(i,nums[itr]+1):
                if j is None or  nums[itr]-k<=j:
                    cnts+=dp(k,nums[itr]-k,itr+1)
            return cnts%MOD

        return dp(0,None,0)
