class Solution {
public:
int mod=1e9+7;
int y(vector<int>&nums,vector<vector<vector<long long int>>>&dp,int i,int last1,int last2){
    if(i==nums.size()){
        return 1;
    }
    if(dp[i][last1][last2]!=-1){
        return dp[i][last1][last2];
    }
    long long int c=0;
    for(int k=0;k<=nums[i];k++){
        if(last1<=k && last2>=nums[i]-k){
            c+=y(nums,dp,i+1,k,nums[i]-k);
                c=c%mod;
            }
        }
    return dp[i][last1][last2]=c%mod;
}
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<long long int>>>dp(nums.size(),vector<vector<long long int>>(52,vector<long long int>(52,-1)));
        return y(nums,dp,0,0,51);
    }
};
