MOD = 10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        dp = [0]*1001
        for i,x in enumerate(nums):
            ndp = [0]*1001
            if i == 0:
                for j in range(x+1):
                    ndp[j] = 1
            else:
                for pa in range(x+1):
                    pb = nums[i-1]-pa
                    for j in range(max(pa,x-pb), x+1):
                        ndp[j] += dp[pa]
                        ndp[j] %= MOD

            dp = ndp

        ans = sum(dp)
        return ans % MOD
