class Solution {

using ll = long long;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        constexpr int MAX_NUM = 50;
        constexpr int MOD = 1e9 + 7;
        vector<vector<ll>> dp(n+1, vector<ll>(MAX_NUM+1, 1));
        for (int i = n-1; i >= 0; --i) {
            for (int prev_first = 0; prev_first <= MAX_NUM; ++prev_first) {
                dp[i][prev_first] = 0;
                int prev_second = i != 0 ? nums[i-1] - prev_first : MAX_NUM;
                for (int first = 0; first <= nums[i]; ++first) {
                    int second = nums[i] - first;
                    if (prev_first > first || prev_second < second) {
                        continue;
                    }
                    dp[i][prev_first] += dp[i+1][first];
                    dp[i][prev_first] %= MOD;
                }
            }
        }
        return dp[0][0];
    }
};
