class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<int>>dp(51,vector<int>(n,0));
        vector<vector<int>>dp1(51,vector<int>(n,0));
        int mod = 1e9+7;
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<=a[i]; j++)
            {
                dp[j][i] = 1;
                dp[a[i]-j][i] = 1;
            }
        }
        for(int i=1; i<n; i++)
        {
            for(int j=0; j<=50; j++)
            {
                if(dp[j][i]==0 ) continue;
                int cnt1 = 0;
                for(int k=j; k>=0; k--)
                {
                    if(a[i-1]-k>=(a[i]-j)) cnt1 = (cnt1+dp[k][i-1])%mod;
                }

                dp[j][i] = cnt1;
            }
        }
        int ans = 0;
        for(int j=0; j<=50; j++)
        {
            ans = (ans+dp[j][n-1])%mod;
        }
        return ans;
    }
};
