class Solution {
public:
    long long mod=1e9+7;
    int dfs(vector<int> &nums,int ind,int prev,vector<vector<long long>> &dp)
    {
        if(ind==nums.size())
            return 1;
        if(dp[ind][prev]!=-1)
            return dp[ind][prev];
        int ans=0;
        for(int i=prev;i<=nums[ind];i++)
        {
            if((ind-1)<0 || (nums[ind-1]-prev)>=(nums[ind]-i))
                ans=(ans%mod+dfs(nums,ind+1,i,dp)%mod)%mod;
        }
        return dp[ind][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) 
    {
        int n=nums.size();
        vector<vector<long long>> dp(n,vector<long long> (51,-1));
        return dfs(nums,0,0,dp);        
    }
};
