class Solution {
public:
    int dp[2001][51][51];  
    const int MOD = 1e9 + 7;

    int solve(int idx, int prev_a, int prev_b, int n, vector<int>& nums) {
        if (idx == n) return 1; 

        if (dp[idx][prev_a][prev_b] != -1) return dp[idx][prev_a][prev_b]; 

        int result = 0;
        for (int a = prev_a; a <= nums[idx]; a++) {  
            int b = nums[idx] - a;  
            if (b >= 0 && b <= prev_b) {  
                result = (result + solve(idx + 1, a, b, n, nums)) % MOD;
            }
        }

        return dp[idx][prev_a][prev_b] = result;  
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));  

        int n = nums.size();
        return solve(0, 0, nums[0], n, nums);  
    }
};
