int mod=(1e9)+7;
int dp[2000][51][51];
int add(long long a,long long b){
    return (a+b)%mod;
}

int solve(vector<int>& nums,int ind,int prev1,int prev2){
    if(ind==nums.size()){
        return 1;
    }
    int ans=0;
    if(dp[ind][prev1][prev2]!=-1){
        return dp[ind][prev1][prev2];
    }
    if(ind==0){
        for(int i=0;i<=nums[ind];i++){
            int j=nums[ind]-i;

             ans=add(ans,solve(nums,ind+1,i,j));
        }
    }else{
        for(int i=prev1;i<=nums[ind];i++){
            int j=nums[ind]-i;
            if(j<=prev2){
               ans=add(ans,solve(nums,ind+1,i,j)); 
            }
        }
    }

   dp[ind][prev1][prev2]=ans;
   return ans;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(nums,0,0,0);
    }
};
