class Solution {
public:
    const int MOD=1e9+7;
    int dp[2002][54][54];
    int solve(int index,int prev1,int prev2,vector<int>&nums) {
        if(index >= nums.size())
        return 1;

        if(dp[index][prev1][prev2]!=-1)
        return dp[index][prev1][prev2];

        int ans=0;
        for(int i=0;i<=nums[index];i++) {
            if(prev1==0 || i >= prev1) {
                int diff=nums[index]-i;
                if(prev2==52 || prev2 >= diff) {
                    ans=(ans+solve(index+1,i,diff,nums))%MOD;
                }
            }
        }
        return dp[index][prev1][prev2]= ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,52,nums);
    }
};
