class Solution {
public:
    const int MOD = 1e9 + 7;
    int dp[2001][51][51];

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return countPairs(nums, 0, 0, 50);
    }

    int countPairs(vector<int>& nums, int i, int last_x, int last_y) {
        if (i == nums.size()) return 1;
        if (dp[i][last_x][last_y] != -1) return dp[i][last_x][last_y];

        int total = 0;

        for (int x = last_x; x <= nums[i]; ++x) {
            int y = nums[i] - x;
            if (y <= last_y) {
                total = (total + countPairs(nums, i + 1, x, y)) % MOD;
            }
        }

        return dp[i][last_x][last_y] = total;
    }
};
