class Solution
{
public:
    int mod = 1e9 + 7;
    int dp[2001][2001];
    int solve(vector<int> &nums, int ind,int last1,int last2)
    {
        if (ind ==nums.size())
        {
            return 1;
        }
        if (dp[last1][ind]!=-1)
        {
            return dp[last1][ind];
        }
        int ans = 0;
        for (int i = last1; i<=nums[ind];i++)
        {
            int j = nums[ind]-i;
            if (j<=last2)
            {
                ans+=solve(nums, ind+1,i,j);
                ans%=mod;
            }
        }
        return dp[last1][ind] = ans;
    }
    int countOfPairs(vector<int> &nums)
    {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return solve(nums,0,0,nums[0]);
    }
};
