class Solution {
public:
    static const int M=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int dp[1001][2];
        int n=nums.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<=1000;j++){
                if(j>nums[i]){
                    dp[j][i&1]=dp[j-1][i&1];continue;
                }
                if(i==0){
                    if(j<=nums[i]){
                        dp[j][i&1]=1;
                    }
                    else{
                        dp[j][i&1]=0;
                    }
                    if(j!=0){
                        dp[j][i&1]+=dp[j-1][i&1];
                    }
                    continue;
                }
                int u=min(nums[i],min(j,nums[i-1]-nums[i]+j));
                if(u>=0){
                    dp[j][i&1]=dp[u][(i^1)&1];
                }
                else{
                    dp[j][i&1]=0;
                }
                if(j!=0){
                    dp[j][i&1]+=dp[j-1][i&1];
                }
                if(dp[j][i&1]>=M){
                    dp[j][i&1]-=M;
                }
            }
        }
        return dp[1000][(n^1)&1];
    }
};
