class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        n = len(nums)
        @cache
        def dfs(i, b):

            if i == n:
                return 1

            if nums[i] < b:
                return 0
            a = nums[i - 1] - b

            sz = 0
            for x in range(min(a, nums[i]) + 1):
                if nums[i] - x < b:
                    break
                sz += dfs(i + 1, nums[i] - x) % mod

            return sz % mod

        ans = sum(dfs(1, x) for x in range(nums[0] + 1)) % mod
        dfs.cache_clear()
        return ans
