class Solution {
public:
    const int m = 1e9 + 7;
    int dp[2001][51];

    int countPairs(int i, int last_val, const vector<int>& nums) {
        if (i == nums.size()) {
            return 1; 
        }
        if (dp[i][last_val] != -1) {
            return dp[i][last_val];
        }
        int result = 0;

        for (int x = last_val; x <= nums[i]; ++x) {
            int y = nums[i] - x;
            if (i == 0 || y <= nums[i-1] - last_val) { 
                result = (result + countPairs(i + 1, x, nums)) %m;
            }
        }

        return dp[i][last_val] = result;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return countPairs(0, 0, nums);
    }
};
