class Solution {
public:
    const int mod = 1e9+7;
    int dp[2001][51][51];
    int rec(vector<int>& nums,int i,int a,int b,int n)
    {
        if(i == n) return 1;


        int p = nums[i]-a;

        if(p<0) return 0;

        if(dp[i][a][b]!=-1)
            return dp[i][a][b];

        int ans = rec(nums,i,a+1,b,n);
        if(p>=0 and p<=b)
        {
            ans = (ans+rec(nums,i+1,a,p,n))%mod;
        }

        return dp[i][a][b] = ans;



    }
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();

        for(int i=0;i<=n;i++)
        {
            for(int j=0;j<=50;j++)
            {
                for(int k=0;k<=50;k++)
                    dp[i][j][k] = -1;
            }
        }

        return rec(nums,0,0,50,n);

    }
};
