class Solution {
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> memo; 

    int solveRec(int ind, int prev1, int prev2, vector<int>& nums) {
        if (ind == nums.size()) {
            return 1;
        }

        if (memo[ind][prev1 + 1][prev2 + 1] != -1) {
            return memo[ind][prev1 + 1][prev2 + 1];
        }

        int cnt = 0;
        for (int i = 0; i <= nums[ind]; i++) {
            int cur1 = i;
            int cur2 = nums[ind] - i;
            if (cur1 >= prev1 && cur2 <= prev2) {
                cnt = (cnt + solveRec(ind + 1, cur1, cur2, nums)) % MOD;
            }
        }

        return memo[ind][prev1 + 1][prev2 + 1] = cnt;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        memo.assign(n + 1, vector<vector<int>>(53, vector<int>(53, -1)));
        return solveRec(0, 0, 51, nums); 
    }
};
