using ll = long long;

ll alg[2005][60];

constexpr ll kMod = 1000'000'007;

class Solution {
public:

//   ll Dfs(int i, int last_1) {
//     if ()
//   }

  int countOfPairs(vector<int>& nums) {
    memset(alg, 0, sizeof(alg));

    int n = nums.size();
    for (int last_1 = 0; last_1 <= 50; ++last_1) {
      alg[n][last_1] = 1;
    }

    for (int i = n - 1; i >= 1; --i) {
      for (int last_1 = 0; last_1 <= nums[i - 1]; ++last_1) {
        int last_2 = nums[i - 1] - last_1;
        alg[i][last_1] = 0;
        for (int num = 0; num <= nums[i]; ++num) {
          if (num >= last_1 && (nums[i] - num) <= last_2) {
            alg[i][last_1] += alg[i + 1][num];
            alg[i][last_1] %= kMod;
          }
        }
      }
    }

    ll res = 0;
    for (int last_1 = 0; last_1 <= nums[0]; ++last_1) {
      res += alg[1][last_1];
      res %= kMod;
    }

    return res;
  }
};
