mod = 10 ** 9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        @cache
        def dp(index, preNum1):
            if index == n: return 1
            res = 0
            for num1 in range(preNum1, nums[index] + 1):
                if index >= 1:
                    preNum2 = nums[index - 1] - preNum1
                    num2 = nums[index] - num1
                    if num2 > preNum2 and num2 >= 0:
                        continue 
                res += dp(index + 1, num1)
                res %= mod
            return res

        return dp(0, 0)
