class Solution {
public:
    int MOD= 1e9+7;
    int dp[2000][51][51];
    int solve(int ind, int prev1, int prev2,vector<int>& nums){
        int n=nums.size();
        if(ind ==n ) return 1;
        if( dp[ind][prev1][prev2]!=-1){
            return  dp[ind][prev1][prev2];
        }
        int curr=nums[ind];
        int ans=0;
        for(int i=0;i<=curr;i++){
            if(i >=prev1 && (curr-i)<=prev2){
                ans=(ans+solve(ind+1, i, curr-i,nums))%MOD;
            }
        }
        return dp[ind][prev1][prev2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,50,nums);
    }
};
