class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        memo = {}

        def countPairs(index, prev1, prev2):
            if index == n:
                return 1
            if (index, prev1, prev2) in memo:
                return memo[(index, prev1, prev2)]

            totalCount = 0
            start = max(prev1, 0)
            end = nums[index]
            for x in range(start, end + 1):
                y = nums[index] - x
                if y <= prev2:
                    totalCount += countPairs(index + 1, x, y)
                    totalCount %= MOD

            memo[(index, prev1, prev2)] = totalCount
            return totalCount

        return countPairs(0, 0, float('inf'))
