class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dp(i, prevArr1, prevArr2):
            if i >= len(nums):
                return 1

            ans = 0
            for j in range(nums[i]+1):
                first = j
                second = nums[i]-j
                if first >= prevArr1 and second <= prevArr2:
                    ans += dp(i+1, first, second)

            return ans

        return dp(0, 0, float('inf')) % (10 ** 9 + 7)
