class Solution {
private:
    int MOD = 1e9 + 7;

    int solve(int ind, int last1, int last2, int n, vector<int>&nums, vector<vector<vector<int>>>&dp)
    {
        if (ind == n) 
        {
            return 1;
        }

        if (dp[ind][last1][last2]!=-1) 
        {
            return dp[ind][last1][last2];
        }

        int count=0;

        for (int x=last1; x<=nums[ind]; x++) 
        {
                int y = nums[ind]-x;

                if(y<=last2) 
                {
                    count=(count + solve(ind+1, x, y, n, nums, dp))%MOD;
                }
        }

        return dp[ind][last1][last2]=count;
    }

public:

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return solve(0, 0, 50, n, nums, dp);
    }
};
