const int mod = 1e9+7;
class Solution {
public:

    int t[2002][52];
    int r(vector<int>& nums,int i,int prev){
        int n=nums.size();
        if(i>=n) return 1;

        if(t[i][prev]!=-1) return t[i][prev];

        if(i==0){
            int ans=0;
            for(int j=0;j<=nums[0];++j){

                int c;
                if(t[i+1][j]!=-1) c=t[i+1][j];
                else c=t[i+1][j]=r(nums,i+1,j);

                ans = (ans+c)%mod;
            }
            return t[i][prev] = ans;
        }

        int ans=0;
        for(int j=prev;j<=nums[i];++j){
            if((nums[i-1]-prev)>=(nums[i]-j)){

                int c;
                if(t[i+1][j]!=-1) c=t[i+1][j];
                else c=t[i+1][j]=r(nums,i+1,j);

                ans = (ans+c)%mod;
            }
        }
        return t[i][prev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(t,-1,sizeof(t));
        return r(nums,0,0);
    }
};
