class Solution {
public:
    int mod= 1e9+7;
    int dp[2002][55][55];
    int f(vector<int> &nums, int ind, int pra,int prb){
        if(ind==nums.size()) return 1;

        if(dp[ind][pra+1][prb+1]!=-1) return dp[ind][pra+1][prb+1];

        int res=0;

        for(int i=0; i<=nums[ind];++i){
            int a= i, b=nums[ind]-i;

            if(pra==-1 || (a>=pra && b<=prb)){
                res= (res+ f(nums, ind+1, a, b)%mod)%mod;
            }
        }

        return dp[ind][pra+1][prb+1]=res;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1, sizeof(dp));
        return f(nums, 0,-1,-1);
    }
};
