int p=1e9+7;

class Solution {

    int n;
    int dp[2002][51];

    int rec(int l,int last,vector<int>& arr){
        if(l==n) return 1;
        if(last>arr[l]) return 0;

        if(dp[l][last]!=-1) return dp[l][last];

        int ans=0, rem=arr[l-1]-last;

        for(int i=last;i<=arr[l];i++){
            if(arr[l]-i>rem) continue;
            ans=(ans+rec(l+1,i,arr))%p;
        }

        return dp[l][last]=ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));

        int ans=0;

        for(int i=0;i<=nums[0];i++){
            ans=(ans+rec(1,i,nums))%p;
        }

        return ans;
    }
};
