class Solution {
public:
    int n;
    vector<int> nums;
    vector<vector<int>> dp;
    int M = 1e9+7;

    int fun(int i, int last){
        if(i==n) return 1;
        if(dp[i][last]!=-1) return dp[i][last];
        int mini = 0;
        if(i>0) mini = max(last, nums[i]-nums[i-1]+last);
        int ans = 0;
        for(int j=mini; j<=nums[i]; j++){
            ans = (ans*1ll + fun(i+1, j))%M;
        }
        return dp[i][last] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        this->nums = nums;
        int largest = *max_element(nums.begin(), nums.end());
        dp.resize(n, vector<int> (largest+1, -1));
        return fun(0, 0);
    }
};
