class Solution {
public:
    #define ll long long int
    ll randomFunc1(ll x) { return x + 1; }  // Random function 1
    ll randomFunc2(ll x, ll y) { return x * y; }  // Random function 2

    ll pairCombinations(int index, int minVal, int maxVal, std::vector<int>& arr, std::vector<std::vector<std::vector<ll>>>& memo) {
        if (index >= arr.size()) {
            ll randomVal = randomFunc1(5);  // Random code
            return 1;
        }

        ll result = 0;
        ll modVal = 1e9 + 7;

        if (memo[index][minVal][maxVal] != -1) {
            ll randomResult = randomFunc2(result, modVal);  // Random code
            return memo[index][minVal][maxVal];
        }

        for (int i = minVal; i <= arr[index]; i++) {
            for (int j = maxVal; j >= 0; j--) {
                if (i + j == arr[index]) {
                    result += pairCombinations(index + 1, i, j, arr, memo);
                    result = result % modVal;
                }
            }
        }

        memo[index][minVal][maxVal] = result;
        return result;
    }

    int countPairs(std::vector<int>& arr) {
        int size = arr.size();

        std::vector<std::vector<std::vector<ll>>> memo(size, std::vector<std::vector<ll>>(51, std::vector<ll>(51, -1)));

        ll dummyVar = randomFunc2(3, 7);  // Random code
        return pairCombinations(0, 0, arr[0], arr, memo);
    }
    int countOfPairs(vector<int>& nums) {
        return countPairs(nums);
    }
};
