class Solution {
public:
    int topDown(vector<int>& nums,vector<vector<vector<int>>>& DP,int i,int j,int k){
        if(i == nums.size())return 1;
        if(DP[i][j][k] != -1)return DP[i][j][k];
        DP[i][j][k]=0;
        for(int p=0;p<=nums[i];p++){
            int q=nums[i]-p;
            if(p < j || q > k)continue;
            DP[i][j][k]=(DP[i][j][k] + topDown(nums,DP,i+1,p,q)) % 1000000007;
        }
        return DP[i][j][k];
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> DP(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return topDown(nums,DP,0,0,50);
    }
};
