/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
  const n = nums.length;
  const MOD = 10 ** 9 + 7;

  const dp = new Array(n).fill().map(() => new Array(53).fill(-1));
  const f = (i, lastY) => {
    if (i === n) return 1;
    if (dp[i][lastY] !== -1) return dp[i][lastY];
    const lastX = i === 0 ? -1 : nums[i - 1] - lastY;

    let count = 0;
    for (let num = 0; num <= nums[i]; num += 1) {
      const potX = num;
      const potY = nums[i] - num;
      if (potX >= lastX && potY <= lastY) {
        count = (count + f(i + 1, potY)) % MOD;
      }
    }

    return dp[i][lastY] = count;
  }

  return f(0, 51);
};

