const MOD: i64 = 1_000_000_007;

impl Solution {
    pub fn count_of_pairs(values: Vec<i32>) -> i32 {
        let size = values.len();
        let mut memo = vec![vec![vec![-1; 51]; 51]; size];

        fn solve(index: usize, val1: usize, val2: usize, values: &Vec<i32>, memo: &mut Vec<Vec<Vec<i64>>>) -> i64 {
            if index >= values.len() {
                return 1;
            }

            if memo[index][val1][val2] != -1 {
                return memo[index][val1][val2];
            }

            let mut result = 0;
            let mod_val = MOD;

            for i in val1..=values[index] as usize {
                for j in (0..=val2).rev() {
                    if i + j == values[index] as usize {
                        result += solve(index + 1, i, j, values, memo);
                        result %= mod_val;
                    }
                }
            }

            memo[index][val1][val2] = result;
            result
        }

        solve(0, 0, values[0] as usize, &values, &mut memo) as i32
    }
}
