class Solution {
public:
    long long mod=1e9+7;
    long long dp[2001][51][51];
    long long dfs(vector<int>&nums,int idx,int a,int b)
    {
        if(nums.size()==idx)
        {
            return 1;
        }
        long long &ans=dp[idx][a][b];
        if(ans!=-1)
        {
            return ans;
        }
        ans=0;
        for(int i=a;nums[idx]-i>=0;i++)
        {
            if((nums[idx]-i)<=b)
            ans+=dfs(nums,idx+1,i,nums[idx]-i);
            ans%=mod;
        }
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return dfs(nums,0,0,nums[0]);
    }
};
