int mod = 1e9+7;
class Solution {
private:
    int f(int ind,int back,vector<int>& nums,vector<vector<int>>& dp){
        if( ind == nums.size() ) return 1;
        int ans=0;
        if( dp[ind][back] !=-1 ) return dp[ind][back];
        if( ind == 0 ){
            for(int val = 0; val<=nums[ind];val++){
                ans=(ans%mod +  f(ind+1,val,nums,dp)%mod)%mod;
            }
        }    
        else{
            int back1 = back;
            int back2 = nums[ind-1]-back1;

            for(int val=0;val<=nums[ind];val++){
                if( val >= back1 && (nums[ind]-val)<=back2){
                    ans=(ans%mod+(f(ind+1,val,nums,dp)%mod))%mod;
                }
            }
        }
        return dp[ind][back]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1,vector<int>(51,-1));
        return f(0,0,nums,dp)%mod;
    }
};
