class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1000000007;
        int n = (int)nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51)));
        for (int i = 0; i <= nums[0]; i++){
            dp[0][i][nums[0] - i] = 1;
        }
        for (int i = 1; i < n; i++){
            for (int i1 = 0; i1 <= 50 && i1 <= nums[i]; i1++){
                int j1 = nums[i] - i1;
                for (int i2 = 0; i2 <= i1 && i2 <= nums[i - 1]; i2++){
                    int j2 = nums[i - 1] - i2;
                    if (j2 < j1) continue;
                    dp[i][i1][j1] = (dp[i][i1][j1] + dp[i - 1][i2][j2]) % mod;
                }
            }
        }

        long long ans = 0;
        for (vector<long long> i : dp.back()){
            for (long long j : i){
                ans = (ans + j) % mod;
            }
        }

        return int(ans);
    }
};
