class Solution {
public:
    long long int mx(int i,int prev,int prev2,vector<vector<vector<long long int>>>&dp,vector<int>&nums,int n,int mod){
        if(i>=n){
            return 1;
        }
        if(dp[i][prev+1][prev2+1]!=-1){
            return dp[i][prev+1][prev2+1];
        }
       long long int ans=0;
        for(int j=0;j<=nums[i];j++){
            if(prev==-1 && prev2==-1){
                ans+=mx(i+1,j,nums[i]-j,dp,nums,n,mod);
                ans%=mod;
            }
            else{
                if(j>=prev && nums[i]-j<=prev2){
                    ans+=mx(i+1,j,nums[i]-j,dp,nums,n,mod);
                    ans%=mod;
                }
            }
        }
        return dp[i][prev+1][prev2+1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<long long int>>>dp(n+1,vector<vector<long long int>>(52,vector<long long int>(52,-1)));
        return mx(0,-1,-1,dp,nums,n,1e9+7);
    }
};
