type LL = i64;
impl Solution {
    fn solve(
        index: usize,
        val1: usize,
        val2: usize,
        values: &Vec<i32>,
        memo: &mut Vec<Vec<Vec<LL>>>,
    ) -> LL {
        if index >= values.len() {
            return 1;
        }

        if memo[index][val1][val2] != -1 {
            return memo[index][val1][val2];
        }

        let mut result = 0;
        let mod_val = 1_000_000_007;

        for i in val1..=values[index] as usize {
            for j in (0..=val2).rev() {
                if i + j == values[index] as usize {
                    result += Solution::solve(index + 1, i, j, values, memo);
                    result %= mod_val;
                }
            }
        }

        memo[index][val1][val2] = result;
        result
    }

    pub fn count_of_pairs(values: Vec<i32>) -> i32 {
        let size = values.len();
        let mut memo = vec![vec![vec![-1; 51]; 51]; size];
        Solution::solve(0, 0, values[0] as usize, &values, &mut memo) as i32
    }
}
