class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)
        min_suffix = list(accumulate(reversed(nums), min))[::-1]

        # print(min_suffix)

        MODULO = 10**9 + 7

        @cache
        def dp(i, prev):
            if i >= n:
                return 1
            xmin = max(prev, nums[i] - nums[i - 1] + prev) if i - 1 >= 0 else prev
            xmax = min_suffix[i]
            ret = 0
            for x in range(xmin, xmax + 1):
                ret = (ret + dp(i + 1, x)) % MODULO
            return ret

        return dp(0, 0)
