class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod=1e9+7,n=nums.size();
        int dp[52][n];
        memset(dp,-1,sizeof(dp));
          function<int(int,int)> f=[&](int p1,int i) -> int {
              if(i==nums.size())return 1;
              if(dp[p1][i]!=-1)return dp[p1][i];
               int cnt=0,p2=51;
              if(i)p2=nums[i-1]-p1;
              for(int j=p1;j<=nums[i];j++){
                  if(nums[i]-j<=p2)cnt=(cnt+f(j,i+1))%mod;
              }
              return dp[p1][i]=cnt;
         };
        return f(0,0);
    }
};
