const int MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), mx = ranges::max(nums), dp[n][mx + 1];
        memset(dp, -1, sizeof(dp));
        auto dfs = [&](auto&& dfs, int i, int pre) {
            if (i == n) return 1;
            if (dp[i][pre] != -1) return dp[i][pre];
            int& res = dp[i][pre]; res = 0;
            for (int j = pre; j <= nums[i]; ++j)
                if (i == 0 || nums[i] - j <= nums[i - 1] - pre)
                    res = (res + dfs(dfs, i + 1, j)) % MOD;
            return res;
        };
        return dfs(dfs, 0, 0);
    }
};
