const int mod=1e9+7;
int dp[2001][52];
int solve(int i,int last,vector<int>&nums){
    int n=nums.size();
    int ans=0;
    if(i<0)return 1;
    if(dp[i][last]!=-1)return dp[i][last];
    for(int x=0;x<=nums[i];x++){
        if(last>=x && ( (i==n-1)|| (nums[i+1]-last <=(nums[i]-x))))
        ans=(ans+(solve(i-1,x,nums)))%mod;

    }
    return dp[i][last] = ans;
}


class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof dp);
        return solve(n-1,51,nums);
    }
};
